(function() {
  var Cassandra, client, connection, thrift, ttypes;
  thrift = require('./lib');
  Cassandra = require('./examples/Cassandra');
  ttypes = require('./examples/cassandra_types');
  connection = thrift.createConnection("localhost", 9162);
  client = thrift.createClient(Cassandra.Client, connection);
  connection.on('error', function(err) {
    return console.error(err);
  });
  client.describe_version(function(err, data) {
    return console.log(['v'], err, data);
  });
  client.batch_mutate("Users", {
    "js2": {
      "User": [
        new ttypes.Mutation({
          column_or_supercolumn: new ttypes.ColumnOrSuperColumn({
            column: new ttypes.Column({
              name: "usernane",
              value: "blah",
              timestamp: Date.now()
            })
          })
        })
      ]
    }
  }, ttypes.ConsistencyLevel.ONE, function() {});
}).call(this);
