var net = require('net');

var start = Date.now();

var stream = net.createConnection(80, 'google.com');

stream.on('timeout', function() {
  console.error('timeout:', Date.now() - start);
  stream.end();
});


stream.on('connect', function() {
  console.log('connect:', Date.now() - start);
  stream.setTimeout(1000);
  //stream.end();
});
